
from pylab import *

def plot(zqd):
    # nianfen = []
    # for i in range(0, 22, 2):
    #     a = i + 2000
    #     nianfen.append(a)
    nianfen = [2000, 2002, 2004, 2006, 2008, 2010, 2012, 2014, 2016, 2018, 2020]

    list1 = [0, 1, 4, 6, 7, 14, 18, 19, 20, 21, 22, 25, 26]
    # list2 = [2, 5, 11, 12, 13, 16, 17]
    # list3 = [3, 8, 9, 10, 15, 23, 24]
    markers = ['o', 's', '^', 'p', '.', 'v',
               ',', 'd', 'h', '2', 'x',
               '4', 'd', '+', 'v', 'x'] # 线条
    plt.clf()
    plt.figure(figsize=(24, 10))
    plt.xticks(nianfen, rotation=45) # 坐标轴文字朝向
    for i, b in zip(list1, markers):
        linshi = []
        for m in zqd:
            linshi.append(m[i])
        plt.plot(nianfen, linshi, marker=b, markersize=6, label='topic_' + str(i + 1))
    plt.legend(loc="best") # 图例
    plt.xlabel('year')
    plt.ylabel('Intensity of theme')
    # plt.savefig('图/人脸识别/强度图/曲折.png') # TODO 图片路径用不用改
    plt.show()
    plt.close()



